import React, { useState } from 'react';
import './App.scss';

function App() {
  const [text , setText]=useState("")

  function getChange(variable) {
    setText(variable.target.value)
  }

  return (
    <div className="App">
      <textarea 
        id="editor" 
        value={text} 
        onChange={getChange}
      ></textarea>
      <div id="preview">{text}</div>
    </div>
  );
}

export default App;
