import React, { useState } from 'react';
import { marked } from 'marked';
import './App.scss';

//a heading element (H1 size), a sub heading element (H2 size), a link, inline code, a code block, a list item, a blockquote, an image, and bolded text.

function App() {
  const [text , setText]=useState(`
  # Welcome to my React Markdown Previewer!
  ## This is a sub-heading...

  There is also [links](https://www.example.com)

  Here is a line of code, \`<div></div>\`, between two backticks.


  \`\`\`
  And here you can see a multiline code
  {
    "firstName": "John",
    "lastName": "Smith",
    "age": 25
  }
  \`\`\` 

  - First item
  - Second item
  - Third item

  > Block Quotes!

  **Bold Text**  

  ![And inserting a image](https://rupicolafree.imgbb.com/)
  `)

  function getChange(variable) {
    setText(variable.target.value)
  }

  return (
    <div className="App">
      <div id="box-editor">

        <div id="header-editor">
          Editor
        </div>

        <textarea 
          rows={10}
          id="editor" 
          value={text} 
          onChange={getChange}
        ></textarea>

      </div>

      <div id="box-preview">

        <div id="header-preview">
            Previewer
        </div>

        <div 
          id="preview"
          dangerouslySetInnerHTML={{__html: marked(text)}}
        ></div>

      </div>

    </div>
  );
}

export default App;
