import React, { useState } from 'react';
import { marked } from 'marked';
import './App.scss';

//a heading element (H1 size), a sub heading element (H2 size), a link, inline code, a code block, a list item, a blockquote, an image, and bolded text.

function App() {
  const [text , setText]=useState(`
  # H1
  ## H2
  [title](https://www.example.com)

  \`code\`

  \`\`\`
  {
    "firstName": "John",
    "lastName": "Smith",
    "age": 25
  }
  \`\`\` 

  - First item
  - Second item
  - Third item

  > blockquote

  ![alt text](image.jpg)

  **bold text**  
  `)

  function getChange(variable) {
    setText(variable.target.value)
  }

  return (
    <div className="App">
      <textarea 
        cols={50}
        rows={10}
        //style={{'background: #c3dada'}}
        id="editor" 
        value={text} 
        onChange={getChange}
      ></textarea>
      <div 
        id="preview"
        dangerouslySetInnerHTML={{__html: marked(text)}}
      ></div>
    </div>
  );
}

export default App;
