import React, { useState } from 'react';
import './App.scss';

function App() {
const [quote,setQuote]= useState("hello World")

function getQuote (){
  setQuote("me has cambiado")
}
  return (
    <div className="App">
      <header className="App-header">
        <p>{quote}</p>
        <button onClick={getQuote}>new quote</button>
      </header>
    </div>
  );
}

export default App;
